mapper.select:
/*①不能写resultMap*/ select count(*) from tbl_study_info a join tbl_patient p on a.patientID=p.id
(a.status=6 or status=4) /*查询的工作 studyID,patientID,patientName,status*/
and a.accessionNumber like '%' #{studyID} '%'
and a.patientID like '%' #{patientID} '%'
and p.name like '%' #{patientName} '%'
and a.status like '%' #{status} '%';
